 5→ */
 6→
 7→import { Hono } from 'hono';
 8→
 9→export const healthRoutes = new Hono();
10→
11→// GET /health — retorna status da API
12→healthRoutes.get('/', (c) => {
13→  return c.json({
14→    status: 'ok',
15→    timestamp: new Date().toISOString(),
16→  });
17→});
