 32→    const data = await res.json();
 33→
 34→    expect(res.status).toBe(200);
 35→    expect(data.tasks).toBeDefined();
 36→    expect(data.total).toBeGreaterThanOrEqual(0);
 37→  });
 38→
 39→  it('POST /tasks cria tarefa com título válido', async () => {
 40→    const res = await app.fetch(
 41→      createRequest('/tasks', {
 42→        method: 'POST',
 43→        headers: { 'Content-Type': 'application/json' },
 44→        body: JSON.stringify({ title: 'Aprender Claude Code' }),
 45→      })
 46→    );
 47→    const data = await res.json();
 48→
 49→    expect(res.status).toBe(201);
 50→    expect(data.id).toBeDefined();
 51→    expect(data.title).toBe('Aprender Claude Code');
 52→    expect(data.completed).toBe(false);
 53→  });
 54→
 55→  it('GET /tasks/:id retorna tarefa criada', async () => {
 56→    // Cria uma tarefa primeiro
 57→    const createRes = await app.fetch(
 58→      createRequest('/tasks', {
 59→        method: 'POST',
 60→        headers: { 'Content-Type': 'application/json' },
 61→        body: JSON.stringify({ title: 'Tarefa para buscar' }),
 62→      })
 63→    );
 64→    const created = await createRes.json();
 65→
 66→    // Busca a tarefa
 67→    const res = await app.fetch(createRequest(`/tasks/${created.id}`));
 68→    const data = await res.json();
 69→
 70→    expect(res.status).toBe(200);
