 79→
 80→  it('DELETE /tasks/:id deleta tarefa existente', async () => {
 81→    // Cria uma tarefa
 82→    const createRes = await app.fetch(
 83→      createRequest('/tasks', {
 84→        method: 'POST',
 85→        headers: { 'Content-Type': 'application/json' },
 86→        body: JSON.stringify({ title: 'Tarefa para deletar' }),
