 1→/**
 2→ * Módulo 1 — API de Tarefas
 3→ *
 4→ * App Hono com CRUD de tarefas (in-memory).
 5→ * Projeto base para os exercícios do workshop.
 6→ */
 7→
 8→import { Hono } from 'hono';
 9→import { tasksRoutes } from './routes/tasks';
10→import { healthRoutes } from './routes/health';
11→
12→const app = new Hono();
13→
14→// Registra rotas
15→app.route('/health', healthRoutes);
16→app.route('/tasks', tasksRoutes);
17→
18→// Rota raiz
19→app.get('/', (c) => {
20→  return c.json({
21→    name: 'Tasks API',
22→    version: '1.0.0',
23→    endpoints: ['/health', '/tasks'],
24→  });
25→});
26→
27→export default app;
28→

 1→/**
 2→ * Tasks CRUD routes
 3→ *
 4→ * API de tarefas com armazenamento in-memory.
 5→ *
 6→ * BUG INTENCIONAL: POST /tasks não valida se o campo "title"
 7→ * está presente ou vazio. Isso permite criar tarefas sem título,
 8→ * o que quebra a listagem e causa problemas downstream.
 9→ *
10→ * Os alunos devem encontrar e corrigir este bug durante o exercício.
11→ */
12→
13→import { Hono } from 'hono';
14→
15→// Tipo de uma tarefa
16→interface Task {
17→  id: string;
18→  title: string;
19→  completed: boolean;
20→  createdAt: string;
21→}
22→
23→// Storage in-memory (reseta a cada deploy)
24→const tasks: Map<string, Task> = new Map();
25→
26→// Contador para IDs sequenciais
27→let nextId = 1;
28→
29→export const tasksRoutes = new Hono();
30→
31→// GET /tasks — lista todas as tarefas
32→tasksRoutes.get('/', (c) => {
33→  const allTasks = Array.from(tasks.values());
34→  return c.json({ tasks: allTasks, total: allTasks.length });
35→});
36→
37→// GET /tasks/:id — busca tarefa por ID
38→tasksRoutes.get('/:id', (c) => {
39→  const id = c.req.param('id');
40→  const task = tasks.get(id);
41→
42→  if (!task) {
43→    return c.json({ error: 'Tarefa não encontrada' }, 404);
44→  }
45→
46→  return c.json(task);
47→});
48→
49→// POST /tasks — cria nova tarefa
50→// BUG: não valida se title está presente ou é string vazia
51→tasksRoutes.post('/', async (c) => {
52→  const body = await c.req.json();
53→
54→  const id = String(nextId++);
55→  const task: Task = {
56→    id,
57→    title: body.title, // BUG: pode ser undefined ou ""
58→    completed: false,
59→    createdAt: new Date().toISOString(),
60→  };
61→
62→  tasks.set(id, task);
63→
64→  return c.json(task, 201);
65→});
66→
67→// DELETE /tasks/:id — deleta tarefa
68→tasksRoutes.delete('/:id', (c) => {
69→  const id = c.req.param('id');
70→
71→  if (!tasks.has(id)) {
72→    return c.json({ error: 'Tarefa não encontrada' }, 404);
73→  }
74→
75→  tasks.delete(id);
76→  return c.json({ deleted: true });
77→});
78→

 1→/**
 2→ * Health check route
 3→ *
 4→ * Endpoint simples para verificar se a API está rodando.
 5→ */
 6→
 7→import { Hono } from 'hono';
 8→
 9→export const healthRoutes = new Hono();
10→
11→// GET /health — retorna status da API
12→healthRoutes.get('/', (c) => {
13→  return c.json({
14→    status: 'ok',
15→    timestamp: new Date().toISOString(),
16→  });
17→});
18→

  1→import { describe, it, expect, beforeEach } from 'vitest';
  2→
  3→/**
  4→ * Testes da Tasks API
  5→ *
  6→ * Alguns testes passam, outros falham intencionalmente.
  7→ * Os alunos devem usar Claude Code para encontrar e corrigir o bug.
  8→ */
  9→
 10→// Importa o app para testes diretos
