 68→    const data = await res.json();
 69→
 70→    expect(res.status).toBe(200);
 71→    expect(data.title).toBe('Tarefa para buscar');
 72→  });
 73→
 74→  it('GET /tasks/:id retorna 404 para tarefa inexistente', async () => {
