/**
 * Zod schemas for content validation (1B.6)
 *
 * Validates JSON files in content/ before syncing to Supabase.
 * Each block type has a specific content schema matching the
 * block_translations.content JSONB structure in the database.
 */

import { z } from "zod/v4";

// ============================================================
// Shared enums and base types
// ============================================================

/** Valid locales matching DB check constraint */
export const LocaleSchema = z.enum(["pt", "en", "es"]);
export type Locale = z.infer<typeof LocaleSchema>;

/** Valid block types matching DB check constraint */
export const BlockTypeSchema = z.enum([
  "text",
  "code",
  "quiz",
  "video",
  "image",
  "callout",
  "exercise",
  "audio",
]);
export type BlockType = z.infer<typeof BlockTypeSchema>;

/** Valid course statuses matching DB check constraint */
export const CourseStatusSchema = z.enum(["draft", "published", "archived"]);

/** Valid module statuses matching DB check constraint */
export const ModuleStatusSchema = z.enum(["draft", "published"]);

// ============================================================
// Block content schemas (per type)
// ============================================================

/** text block: markdown body */
export const TextContentSchema = z.object({
  body: z.string().min(1),
});

/** code block: source code with language and optional caption */
export const CodeContentSchema = z.object({
  body: z.string().min(1),
  language: z.string().min(1),
  caption: z.string().optional(),
});

/** quiz block: question with multiple choice options */
export const QuizContentSchema = z.object({
  question: z.string().min(1),
  options: z.array(z.string().min(1)).min(2),
  correct: z.number().int().min(0),
  explanation: z.string().optional(),
});

/** video block: URL with optional transcript */
export const VideoContentSchema = z.object({
  url: z.string().url(),
  transcript: z.string().optional(),
});

/** image block: URL with alt text and optional caption */
export const ImageContentSchema = z.object({
  url: z.string().min(1),
  alt: z.string().min(1),
  caption: z.string().optional(),
});

/** callout block: styled box with variant (info/warning/tip) */
export const CalloutContentSchema = z.object({
  body: z.string().min(1),
  variant: z.enum(["info", "warning", "tip"]),
});

/** exercise block: structured fields for premium rendering */
export const ExerciseContentSchema = z.object({
  title: z.string().min(1),
  estimated_time: z.number().int().min(1).optional(),
  instructions: z.string().min(1),
  observations: z.string().optional(),
  context: z.string().optional(),
  starter_code: z.string().optional(),
  solution: z.string().optional(),
});

/**
 * audio block: audio clip extracted from workshop recordings.
 *
 * Used for self-service ReadingView — embeds instructor voice
 * clips that complement the written content of a section.
 */
export const AudioContentSchema = z.object({
  /** Public URL or Supabase Storage path to the audio file (mp3/m4a) */
  src: z.string().min(1),
  /** Display title for the audio player */
  title: z.string().min(1),
  /** Duration in seconds (for UI display) */
  duration_seconds: z.number().int().min(1).optional(),
  /** Optional transcript of the audio clip */
  transcript: z.string().optional(),
  /** Brief description shown below the player */
  description: z.string().optional(),
});

/**
 * audio_clip meta field — can be added to ANY block type.
 *
 * References a segment of a workshop recording that relates
 * to the block's content. Used to offer "hear the instructor
 * explain this" in the ReadingView self-service experience.
 *
 * Usage in block meta:
 *   "audio_clip": {
 *     "src": "supabase-storage-path-or-url",
 *     "title": "Explicação do Agent Loop",
 *     "timestamp_start": "19:48",
 *     "timestamp_end": "29:06",
 *     "source_recording": "modulo-1-2026-02-19"
 *   }
 */
export const AudioClipMetaSchema = z.object({
  src: z.string().min(1),
  title: z.string().min(1),
  timestamp_start: z.string().optional(),
  timestamp_end: z.string().optional(),
  source_recording: z.string().optional(),
});

/** Map of block type to its content schema */
export const BlockContentSchemas: Record<BlockType, z.ZodType> = {
  text: TextContentSchema,
  code: CodeContentSchema,
  quiz: QuizContentSchema,
  video: VideoContentSchema,
  image: ImageContentSchema,
  callout: CalloutContentSchema,
  exercise: ExerciseContentSchema,
  audio: AudioContentSchema,
};

// ============================================================
// Block meta schemas (per type, optional config)
// ============================================================

/** Meta for code blocks */
export const CodeMetaSchema = z.object({
  language: z.string().optional(),
  runnable: z.boolean().optional(),
});

/** Meta for video blocks */
export const VideoMetaSchema = z.object({
  provider: z.string().optional(),
  duration_seconds: z.number().optional(),
});

/** Meta for quiz blocks */
export const QuizMetaSchema = z.object({
  passing_score: z.number().optional(),
});

// ============================================================
// Block entry in blocks.{locale}.json
// ============================================================

/**
 * A single block entry in a blocks file.
 *
 * The `meta` object may contain optional section navigation fields:
 * - `section_group` (string): kebab-case slug identifying the section
 *   (e.g., "agent-loop", "claude-md"). Same across all locales for
 *   the same module — language-agnostic identifier.
 * - `section_title` (string): human-readable section title, localized
 *   per language file (e.g., "O Agent Loop" in PT, "The Agent Loop" in EN).
 *
 * These fields are used by the SectionView/SectionNav components to
 * group blocks into navigable sections within the learning experience.
 */
export const BlockEntrySchema = z.object({
  slug: z.string().min(1),
  type: BlockTypeSchema,
  meta: z.record(z.string(), z.unknown()).optional(),
  content: z.record(z.string(), z.unknown()),
});
export type BlockEntry = z.infer<typeof BlockEntrySchema>;

/** Array of block entries (one blocks.{locale}.json file) */
export const BlocksFileSchema = z.array(BlockEntrySchema).min(1);

// ============================================================
// Meta file schemas
// ============================================================

/** course meta.json — course-level config */
export const CourseMetaSchema = z.object({
  slug: z.string().min(1),
  status: CourseStatusSchema.default("draft"),
  created_by: z.string().optional(),
});
export type CourseMeta = z.infer<typeof CourseMetaSchema>;

/** course meta.{locale}.json — course translation */
export const CourseTranslationSchema = z.object({
  title: z.string().min(1),
  description: z.string().optional(),
});
export type CourseTranslation = z.infer<typeof CourseTranslationSchema>;

/** module meta.json — module-level config */
export const ModuleMetaSchema = z.object({
  slug: z.string().min(1),
  sort_order: z.number().int().min(0),
  status: ModuleStatusSchema.default("draft"),
  duration_minutes: z.number().int().optional(),
});
export type ModuleMeta = z.infer<typeof ModuleMetaSchema>;

/** module meta.{locale}.json — module translation */
export const ModuleTranslationSchema = z.object({
  title: z.string().min(1),
  description: z.string().optional(),
});
export type ModuleTranslation = z.infer<typeof ModuleTranslationSchema>;

// ============================================================
// Validation helper
// ============================================================

/**
 * Validates block content against its type-specific schema.
 * Returns { success: true, data } or { success: false, error }.
 */
export function validateBlockContent(
  type: BlockType,
  content: unknown
): z.SafeParseReturnType<unknown, unknown> {
  const schema = BlockContentSchemas[type];
  return schema.safeParse(content);
}

