    "slug": "recursos-e-prompts",
    "type": "code",
    "meta": {
      "language": "typescript",
      "section_group": "anatomia-mcp",
      "section_title": "Anatomia de um MCP Server"
    },
    "content": {
      "body": "import { ResourceTemplate } from "@modelcontextprotocol/sdk/server/mcp.js";

// Resource: dados que Claude pode ler sob demanda
server.resource(
  "notas",
  new ResourceTemplate("notas://{id}", { list: undefined }),
  async (uri, { id }) => ({
    contents: [{
      uri: uri.href,
      text: `Conteúdo da nota ${id}`,
    }],
  })
);

// Prompt: template reutilizável invocado pelo usuário
server.prompt(
  "resumir",
  { texto: z.string() },
  ({ texto }) => ({
    messages: [{
      role: "user",
      content: {
        type: "text",
        text: `Resuma este texto em 3 bullets:\n\n${texto}`,
      },
    }],
  })
);",
      "language": "typescript",
      "caption": "Resources e Prompts — os outros dois primitivos. Menos usados que Tools, mas úteis em cenários específicos."
    }
